// 인덱싱 시작 로그
console.log('🚀 코드베이스 인덱싱 시작...');
console.log('📁 대상 경로: /data/shared/cloned_repo');

return {
  status: 'starting',
  timestamp: new Date().toISOString(),
  target_path: '/data/shared/cloned_repo',
  note: 'Git 리포지토리는 미리 클론되어 있어야 합니다'
};

// 리포지토리 존재 여부 확인
const checkResult = $('Check Repository').first().json;

if (checkResult.exitCode !== 0) {
  throw new Error('❌ 리포지토리가 /data/shared/cloned_repo 경로에 없습니다. 먼저 로컬에서 클론해주세요.');
}

console.log('✅ 리포지토리 확인 완료');
console.log('📂 경로: /data/shared/cloned_repo');

return {
  status: 'repository_found',
  path: '/data/shared/cloned_repo',
  check_result: checkResult.stdout
};

// 파일 목록 처리
const findResult = $('Find Source Files').first().json;

if (findResult.exitCode !== 0) {
  throw new Error(`파일 검색 실패: ${findResult.stderr}`);
}

const fileList = findResult.stdout.trim().split('\n').filter(f => f.length > 0);
console.log(`📁 발견된 소스 파일: ${fileList.length}개`);

if (fileList.length === 0) {
  throw new Error('❌ Swift 또는 Kotlin 파일을 찾을 수 없습니다.');
}

// 처음 10개 파일만 처리 (테스트용)
const limitedFiles = fileList.slice(0, 10);
console.log(`🎯 처리할 파일: ${limitedFiles.length}개 (테스트용 제한)`);

// 각 파일을 개별 아이템으로 반환
return limitedFiles.map((filePath, index) => ({
  filePath: filePath,
  fileName: filePath.split('/').pop(),
  fileType: filePath.endsWith('.swift') ? 'swift' : 'kotlin',
  fileIndex: index + 1,
  totalFiles: limitedFiles.length
}));

// 파일 내용 읽기 및 청크 분할
const fs = require('fs');
const fileInfo = $json;
const filePath = fileInfo.filePath;

console.log(`📄 처리 중: ${fileInfo.fileName} (${fileInfo.fileIndex}/${fileInfo.totalFiles})`);

try {
  // 파일 내용 읽기
  const content = fs.readFileSync(filePath, 'utf8');

  // 너무 작은 파일은 스킵
  if (content.length < 50) {
    console.log(`⏭️  파일 스킵 (너무 작음): ${fileInfo.fileName}`);
    return [];
  }

  // 텍스트 청킹 (1000자 단위, 100자 오버랩)
  const chunkSize = 1000;
  const chunkOverlap = 100;
  const chunks = [];

  if (content.length <= chunkSize) {
    // 파일이 작으면 전체를 하나의 청크로
    chunks.push({
      text_chunk: content.trim(),
      chunk_index: 0,
      file_path: filePath,
      file_name: fileInfo.fileName,
      file_type: fileInfo.fileType,
      chunk_size: content.length,
      total_chunks: 1
    });
  } else {
    // 큰 파일은 오버랩을 고려해서 분할
    let start = 0;
    let chunkIndex = 0;

    while (start < content.length) {
      const end = Math.min(start + chunkSize, content.length);
      const chunk = content.slice(start, end).trim();

      if (chunk.length > 0) {
        chunks.push({
          text_chunk: chunk,
          chunk_index: chunkIndex,
          file_path: filePath,
          file_name: fileInfo.fileName,
          file_type: fileInfo.fileType,
          chunk_size: chunk.length,
          total_chunks: 'calculating...'
        });
        chunkIndex++;
      }

      start = end - chunkOverlap;
    }

    // 총 청크 수 업데이트
    chunks.forEach(chunk => chunk.total_chunks = chunks.length);
  }

  console.log(`📊 ${fileInfo.fileName}: ${chunks.length}개 청크 생성`);
  return chunks;

} catch (error) {
  console.error(`❌ 파일 읽기 실패 ${filePath}: ${error.message}`);
  return [];
}

// 임베딩 결과 처리 및 Qdrant 포인트 준비
const chunkData = $('Read File & Create Chunks').first().json;
const embeddingResponse = $json;

// 임베딩 벡터 추출
const embedding = embeddingResponse[0]; // 첫 번째 임베딩

if (!embedding || !Array.isArray(embedding)) {
  throw new Error('❌ 임베딩 응답이 유효하지 않습니다');
}

if (embedding.length !== 384) {
  throw new Error(`❌ 임베딩 차원이 맞지 않습니다: ${embedding.length} (예상: 384)`);
}

// Qdrant 포인트 생성
const crypto = require('crypto');
const pointId = crypto.randomUUID();

const qdrantPoint = {
  id: pointId,
  vector: embedding,
  payload: {
    text_chunk: chunkData.text_chunk,
    file_path: chunkData.file_path,
    file_name: chunkData.file_name,
    file_type: chunkData.file_type,
    chunk_index: chunkData.chunk_index,
    total_chunks: chunkData.total_chunks,
    chunk_size: chunkData.chunk_size,
    indexed_at: new Date().toISOString()
  }
};

console.log(`🔢 임베딩 생성 완료: ${chunkData.file_name} [${chunkData.chunk_index}/${chunkData.total_chunks}]`);

return qdrantPoint;

// 저장 결과 확인 및 로깅
const qdrantResponse = $json;
const chunkData = $('Prepare Qdrant Point').first().json;

if (qdrantResponse.status === 'ok') {
  console.log(`✅ Qdrant 저장 완료: ${chunkData.payload.file_name} [${chunkData.payload.chunk_index}/${chunkData.payload.total_chunks}]`);

  return {
    status: 'success',
    point_id: chunkData.id,
    file_name: chunkData.payload.file_name,
    chunk_index: chunkData.payload.chunk_index,
    total_chunks: chunkData.payload.total_chunks,
    stored_at: new Date().toISOString()
  };
} else {
  console.error(`❌ Qdrant 저장 실패: ${JSON.stringify(qdrantResponse)}`);
  throw new Error(`Qdrant 저장 실패: ${JSON.stringify(qdrantResponse)}`);
}

// 임베딩 실패 처리
const chunkData = $('Read File & Create Chunks').first().json;

console.error(`❌ 임베딩 생성 실패: ${chunkData.file_name}`);
console.error('💡 힌트: 임베딩 서비스가 실행 중인지 확인하세요');

return {
  error: 'Embedding generation failed',
  file_name: chunkData.file_name,
  chunk_index: chunkData.chunk_index,
  timestamp: new Date().toISOString(),
  suggestion: 'Check if embedding service is running at http://localhost:8080'
};
