const fs = require('fs');
const path = require('path');

const fileInfo = $json;
const filePath = fileInfo.filePath;

try {
  // íŒŒì¼ ë‚´ìš© ì½ê¸°
  const content = fs.readFileSync(filePath, 'utf8');

  // í…ìŠ¤íŠ¸ ì²­í‚¹ (1000ì ë‹¨ìœ„, 100ì ì˜¤ë²„ë©)
  const chunkSize = 1000;
  const chunkOverlap = 100;
  const chunks = [];

  if (content.length <= chunkSize) {
    // íŒŒì¼ì´ ì‘ìœ¼ë©´ ì „ì²´ë¥¼ í•˜ë‚˜ì˜ ì²­í¬ë¡œ
    chunks.push({
      text_chunk: content,
      chunk_index: 0,
      file_path: filePath,
      file_name: fileInfo.fileName,
      file_type: fileInfo.fileType,
      chunk_size: content.length
    });
  } else {
    // í° íŒŒì¼ì€ ì˜¤ë²„ë©ì„ ê³ ë ¤í•´ì„œ ë¶„í• 
    let start = 0;
    let chunkIndex = 0;

    while (start < content.length) {
      const end = Math.min(start + chunkSize, content.length);
      const chunk = content.slice(start, end);

      chunks.push({
        text_chunk: chunk,
        chunk_index: chunkIndex,
        file_path: filePath,
        file_name: fileInfo.fileName,
        file_type: fileInfo.fileType,
        chunk_size: chunk.length
      });

      start = end - chunkOverlap;
      chunkIndex++;
    }
  }

  console.log(`ğŸ“„ ${fileInfo.fileName}: ${chunks.length}ê°œ ì²­í¬ ìƒì„±`);
  return chunks;

} catch (error) {
  console.error(`âŒ íŒŒì¼ ì½ê¸° ì‹¤íŒ¨ ${filePath}: ${error.message}`);
  return [];
}

